CREATE PROCEDURE SP3S_RECREATE_INFSCHEMA_COLS
AS
BEGIN
   IF NOT EXISTS (SELECT TOP 1 * FROM INF_SCHEMA_COLUMNS (NOLOCK))
	 INSERT INF_SCHEMA_COLUMNS	( CHARACTER_MAXIMUM_LENGTH, CHARACTER_OCTET_LENGTH, CHARACTER_SET_CATALOG, CHARACTER_SET_NAME, CHARACTER_SET_SCHEMA, COLLATION_CATALOG, COLLATION_NAME, COLLATION_SCHEMA, COLUMN_DEFAULT, COLUMN_NAME, DATA_TYPE, DATETIME_PRECISION, DOMAIN_CATALOG, DOMAIN_NAME, DOMAIN_SCHEMA, IS_NULLABLE, NUMERIC_PRECISION, NUMERIC_PRECISION_RADIX, NUMERIC_SCALE, ORDINAL_POSITION, TABLE_CATALOG, TABLE_NAME, TABLE_SCHEMA )  
	 SELECT 	  CHARACTER_MAXIMUM_LENGTH, CHARACTER_OCTET_LENGTH, CHARACTER_SET_CATALOG, CHARACTER_SET_NAME, CHARACTER_SET_SCHEMA, COLLATION_CATALOG, COLLATION_NAME, COLLATION_SCHEMA, COLUMN_DEFAULT, COLUMN_NAME, DATA_TYPE, DATETIME_PRECISION, DOMAIN_CATALOG, DOMAIN_NAME, DOMAIN_SCHEMA, IS_NULLABLE, NUMERIC_PRECISION, NUMERIC_PRECISION_RADIX, NUMERIC_SCALE, ORDINAL_POSITION, TABLE_CATALOG, TABLE_NAME, TABLE_SCHEMA 
	 FROM INFORMATION_SCHEMA.COLUMNS
END

 