
IF NOT  EXISTS (SELECT TOP 1 'U' FROM PPC_MODULES WHERE FORM_NAME='GETISSUERMVIEW.ASPX' AND FORM_OPTION='RAW_MATERIAL')
BEGIN  
   	INSERT PPC_MODULES	( USER_CODE, FORM_NAME, FORM_OPTION, VALUE, ROW_ID, LAST_UPDATE, GROUP_NAME, S_NO, DISPLAY_FORM_NAME, DISPLAY_NAME, DEPT_ID )  
    SELECT '0000000' AS USER_CODE ,'GETISSUERMVIEW.ASPX' AS FORM_NAME,'RAW_MATERIAL' AS FORM_OPTION,'1' AS VALUE,NEWID()  AS ROW_ID,GETDATE()  AS LAST_UPDATE,'TRANSACTION' AS GROUP_NAME,252   AS  S_NO ,'ISSUERMAGAINSTCUTTINGORDER' AS DISPLAY_FORM_NAME,'RAW_MATERIAL' AS DISPLAY_NAME,'' AS DEPT_ID  UNION ALL
    SELECT '0000000' AS USER_CODE ,'GETISSUERMVIEW.ASPX' AS FORM_NAME,'FABRIC' AS FORM_OPTION,'1' AS VALUE,NEWID()  AS ROW_ID,GETDATE()  AS LAST_UPDATE,'TRANSACTION' AS GROUP_NAME,253   AS  S_NO ,'ISSUERMAGAINSTCUTTINGORDER' AS DISPLAY_FORM_NAME,'FABRIC' AS DISPLAY_NAME,'' AS DEPT_ID  UNION ALL
    SELECT '0000000' AS USER_CODE ,'GETISSUERMVIEW.ASPX' AS FORM_NAME,'TRIMS' AS FORM_OPTION,'1' AS VALUE,NEWID()  AS ROW_ID,GETDATE()  AS LAST_UPDATE,'TRANSACTION' AS GROUP_NAME,254   AS  S_NO ,'ISSUERMAGAINSTCUTTINGORDER' AS DISPLAY_FORM_NAME,'TRIMS' AS DISPLAY_NAME,'' AS DEPT_ID  UNION ALL
    SELECT '0000000' AS USER_CODE ,'GETISSUERMVIEW.ASPX' AS FORM_NAME,'PACKING_MATERIAL' AS FORM_OPTION,'1' AS VALUE,NEWID()  AS ROW_ID,GETDATE()  AS LAST_UPDATE,'TRANSACTION' AS GROUP_NAME,255   AS  S_NO ,'ISSUERMAGAINSTCUTTINGORDER' AS DISPLAY_FORM_NAME,'PACKING_MATERIAL' AS DISPLAY_NAME,'' AS DEPT_ID   
 END
